import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angulars';
  name:string='Uday More'
  value:any='';
  count:number=0;
  disabled=false;

  show=true;
  onSubmit(submitVal:any)
  {
   submitVal=='-'?this.count--:this.count++;
  }

  onClick(val:any)
  {
    this.value= val;

  }
}
