<h1 *ngIf="show=='yes'; then ifcondition else elseBlock">
</h1>
<ng-template #ifcondition>
  <h1>If block</h1>
</ng-template>
<ng-template #elseBlock>`

  <h1>Else condition executed</h1>

</ng-template>
<h1 *ngIf="hide; then showButton else showInputBox"></h1>
<ng-template #showButton>
  <button  type="submit" (click)="hideButton(false)" >Button</button></ng-template>
  <ng-template #showInputBox>
<input type="text" placeholder="you learned the if esle">
  </ng-template>
  <ng-template [ngIf]="color=='red'">
    <h1 style="color:red;">Color is red</h1>

  </ng-template>
  <ng-template [ngIf]="color=='green'">
    <h1 style="color:green;">Color is green</h1>

