import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angulars';
  name:string='Uday More'
  value:any='';
  count:number=0;
  disabled=false;
hide=true;
color='red';
  show='yes';
   users =['Anil','Bother','Head']
   usersDetail=[{
    name:'Uday',
    email:'udaymore437@gmail.com'

   },{
    name:'Sam',
    email:'Sam@gmail.com'

   }]

   customer=[
    {
   name:'Aman',
   address:'Mumbai',
   customersocialAccount:[{ln:'linkedin'},{fl:'facebook'}]
    },{
      name:'Bhoir',
      address:'Bihar',
      customersocialAccount:[{ln:'linkedin'},{fl:'facebook'}]
    }
   ]
  hideButton(val:boolean)
  {
    this.hide=val
  }
  onSubmit(submitVal:any)
  {
   submitVal=='-'?this.count--:this.count++;
  }

  onClick(val:any)
  {
    this.value= val;

  }
}
<h1>Welcome to Angular  {{name}}</h1>
<!-- <input type="text" #box (click)="onClick(box.value)" placeholder="Enter tour name here" name="name">
<input type="text" #bind placeholder="Property binding" value={{name}} disabled="{{disabled}}" >
<input type="text" #bind1 placeholder="Property binding" [value]=" name" [disabled]="disabled">
<button type="submit" #box1 (click)="onSubmit('-')">-</button>
<button type="submit" #box2 (click)="onSubmit('+')">+</button>
<p>Count is {{count}}</p>
<p>My name is {{value}}</p> -->

<h1 *ngIf="show=='yes'; then ifcondition else elseBlock">
</h1>
<ng-template #ifcondition>
  <h1>If block</h1>
</ng-template>
<ng-template #elseBlock>`

  <h1>Else condition executed</h1>

</ng-template>
<h1 *ngIf="hide; then showButton else showInputBox"></h1>
<ng-template #showButton>
  <button  type="submit" (click)="hideButton(false)" >Button</button></ng-template>
  <ng-template #showInputBox>
<input type="text" placeholder="you learned the if esle">
  </ng-template>
  <ng-template [ngIf]="color=='red'">
    <h1 style="color:red;">Color is red</h1>

  </ng-template>
  <ng-template [ngIf]="color=='green'">
    <h1 style="color:green;">Color is green</h1>

  </ng-template>
  <div [ngSwitch]="color">
    <h1 *ngSwitchCase="'red'" style="color:red" >switch case red</h1> 
    <h1 *ngSwitchCase="'green'" style="color: green"> switch</h1>

  </div>
<h1 *ngFor="let item of users">{{item}}</h1>
<h1 *ngFor="let item of usersDetail">{{item.name}}==>{{item.email}}</h1>

  <ul *ngFor="let customer of customer">
    <li>{{customer.name}}</li>
    <li>{{customer.address}}</li>
    <ul *ngFor ="let account of customer.customersocialAccount">
      <li>{{account.ln}}</li>
      <li>{{account.fl}}</li>
    </ul>
  </ul>

  <h1 [style.color]="color">Hello dynamic binding</h1>
