import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angulars';
  name:string='Uday More'
  value:any='';
  count:number=0;
  disabled=false;
  onSubmit(submitVal:any)
  {
   submitVal=='-'?this.count--:this.count++;
  }
  onClick(val:any)
  {
    this.value= val;

  }
}
<h1>Welcome to Angular  {{name}}</h1>
<input type="text" #box (click)="onClick(box.value)" placeholder="Enter tour name here" name="name">
<input type="text" #bind placeholder="Property binding" value={{name}} disabled="{{disabled}}" >
<input type="text" #bind1 placeholder="Property binding" [value]=" name" [disabled]="disabled">
<button type="submit" #box1 (click)="onSubmit('-')">-</button>
<button type="submit" #box2 (click)="onSubmit('+')">+</button>
<p>Count is {{count}}</p>
<p>My name is {{value}}</p>
